import { Logout } from "../../axios/api";
import { Inform } from "../../assets/common";
export default {
  name: "LogOut",
  data: function data() {
    return {
      spinning: false
    };
  },
  created: function created() {
    var _this = this;

    this.spinning = true;
    var login_user = {
      'user_id': this.$cookie.get('user_id'),
      'user_name': this.$cookie.get('user_name'),
      'user_access_token': this.$cookie.get('user_access_token')
    };
    Logout(login_user).then(function (res) {
      if (res.data.code == 1) {
        _this.$cookie.delete('user_id');

        _this.$cookie.delete('user_name');

        _this.$cookie.delete('user_access_token'); //单点登录-同步通知接口
        // Inform(res.data.data);


        _this.$message.success(res.data.msg, 2);

        _this.$router.replace({
          path: '/login'
        });
      } else {
        _this.$message.error(res.data.msg, 2);

        _this.$router.replace({
          path: '/index'
        });
      }

      _this.spinning = false;
    });
  }
};
